import React, {Component} from 'react';
import PubSub from 'pubsub-js'
import "./index.css"
import spinner from "../../../../hooks-movie-app-master1/src/assets/ajax-loader.gif";


export default class Movie extends Component {
    state={
        users: [],
        isLoading: false,
    }
    componentDidMount(){
        this.token=PubSub.subscribe('movies',(_, stateObj)=>{
            this.setState(stateObj)
        })
    }
    componentWillUnmount() {
        PubSub.unsubscribe(this.token)
    }


    render() {
        const {users,isLoading}=this.state
        return (
            <div>
                {
                    isLoading ?  <img className="spinner" src={spinner} alt="Loading spinner" />:
                    users.map((userObj)=>{
                        return (
                            <div key={userObj.imdbID} className={'showMovies'}>
                                <img alt={'movie'} src={userObj.Poster} style={{width:'100px'}}/>
                                <p id={userObj.imdbID}>{userObj.Title}</p>
                                <input type={'checkbox'} />
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}
