import React, {Component} from 'react';
import "./index.css"
import PubSub from 'pubsub-js'
import axios from 'axios'

export default class Search extends Component {


    handleSearchValue =({search})=>{
        const [searchValue,setsearchValue]=useState('')
        const handleSearchValueInput =e =>{
            setsearchValue(e.target.value)
        }
        const resetInputField=()=>{
            setsearchValue('')
        }
        const handleSearchValue= e =>{
            e.preventDefault();
            search(searchValue);
            resetInputField();
        }
    }

    render() {
        return (
            <div>
                <form className={"search"}>
                    <input  value={searchValue} onChange={handleSearchValueInput} type={"text"} className={"SearchInput"} placeholder={"Search your movie!"}/>
                    <input  onClick={handleSearchValue} type={"submit"} className={"SearchButton"} value={"Search"}/>
                </form>
            </div>
        );
    }
}
